from flask import Flask, render_template, request, redirect

app = Flask(__name__)

# Dummy data for the arts collection
arts_collection = [
    {
    'TITLE': 'THE PERSISTANCE OF MEMEORY',
    'artist': 'SALVADOR DALI',
    'image_url': 'data:image/jpeg;base64,'
},
  
{
    'title': 'THE STARRY NIGHT',
    'artist': 'VINCENT VAN VOGH',
    'image_url': 'https://upload.wikimedia.org/wikipedia/commons/thumb/e/ea/Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg/350px-Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg'
},
 {
    'title': 'MONA LISA',
    'artist': 'LEONARDO DA VINCI',
    'image_url': 'https://uploads0.wikiart.org/00339/images/leonardo-da-vinci/mona-lisa-c-1503-1519.jpg'
},

]

@app.route('/')
def home():
    return render_template('index.html', arts=arts_collection)

@app.route('/add_art', methods=['GET', 'POST'])
def add_art():
    if request.method == 'POST':
        title = request.form.get('title')
        artist = request.form.get('artist')
        image_url = request.form.get('image_url')

        # Create a new art object
        new_art = {
            'title': title,
            'artist': artist,
            'image_url': image_url
        }

        # Add the new art object to the collection
        arts_collection.append(new_art)

        return redirect('/')
    
    return render_template('add_art.html')

if __name__ == '_main_':
    app.run(debug=True)